mapper.select:
select studentNo , studentName , sex , phone , address , age from student

    and studentNo = #{studentNo}
    and studentName = #{studentName}
    and sex = #{sex}
    and phone = #{phone}
    and address = #{address}
    and age = #{age}

select courseNo , courseName , detail from selectCourse 
    inner join course on selectCourse.courerNo = course.courseNo
    where studentNo = #{studentNo}

mapper.update:
update student set

    , studentName = #{studentName}
    , sex = #{sex}
    , phone = #{phone}
    , address = #{address}
    , age = #{age}
